<?php

class Solution
{

    /**
     * @desc 比较输入的两个字符串长度，短的在字符串开头补零到长度和长的字符串一致， 然后从最后一位开始相加， 记录相加结果是否进位即可
     * @user chenlei
     * @param string $num1
     * @param string $num2
     * @return string
     */
    function addStrings($num1, $num2) {
        $result = '';
        $len1 = strlen($num1);
        $len2 = strlen($num2);
        if ($len1 - $len2 >= 0) {
            $fill_zero = $len1 - $len2;
            $num2 = str_repeat('0', $fill_zero) . $num2;
        } else {
            $fill_zero = $len2 - $len1;
            $num1 = str_repeat('0', $fill_zero) . $num1;
        }
        echo 'num1:', $num1, ' num2:', $num2, PHP_EOL;
        $len = strlen($num1);
        $carry = 0;
        for ($i = $len; $i > 0; $i--) {
            $count = substr($num1, $i - 1, 1) + substr($num2, $i - 1, 1);
            echo 'result:', $result, PHP_EOL;
            echo 'count:', $count, PHP_EOL;
            if ($carry) {
                $count++;
                $carry = 0;
            }
            if ($count >= 10) {
                $result = (string)($count - 10) . $result;
                $carry = 1;
            } else {
                $result = (string)($count) . $result;
            }
        }
        if ($carry) {
            $result = (string)($carry) . $result;
        }
        return $result;
    }
}

$num1 = "";
$num2 = "";

$sol = new Solution();
echo $sol->addStrings($num1, $num2);