class Solution {

    /**
     * @param Integer[] $nums
     * @return Integer[][]
     */
    static function threeSum($nums) {
        $len = count($nums);
        $result = [];
        sort($nums);
        for ($i = 0; $i < $len - 2; $i++) {
            $flag = $nums[$i];
            if ($flag > 0) {
                break;
            }
            if ($i > 0 && $nums[$i] == $nums[$i - 1]) {
                continue;
            }
            $left = $i + 1;
            $right = $len - 1;
            while ($left < $right) {
                $sum = $flag + $nums[$left] + $nums[$right];
                if ($sum === 0) {
                    $result[] = [$flag, $nums[$left], $nums[$right]];
                    while ($left < $right && $nums[$left] === $nums[$left + 1]) {
                        $left++;
                    }
                    while ($left < $right && $nums[$right] === $nums[$right - 1]) {
                        $right--;
                    }
                    $right--;
                    $left++;
                } else if ($sum > 0) {
                    $right--;
                } else {
                    $left++;
                }
            }
        }
        return $result;
    }
}

